import React from "react";
import {
  GoogleMap,
  useLoadScript,
  Marker,
  InfoWindow,
} from "@react-google-maps/api";
import usePlacesAutocomplete, {
  getGeocode,
  getLatLng,
} from "use-places-autocomplete";
import {
  Combobox,
  ComboboxInput,
  ComboboxPopover,
  ComboboxList,
  ComboboxOption,
} from "@reach/combobox";
import { formatRelative } from "date-fns";

import "@reach/combobox/styles.css";
import { googleAPIKey, placeType } from "./components/GooglePlacesAPI";

const libraries = ["places"];
const mapContainerStyle = {
  width: "100vw",
  height: "50vh",
};

function getCoords(lng, lat) {
  var posit = {
    lat: lat,
    lng: lng,
  };
  return;
}

function getPosition() {
  if (navigator.geolocation) {
    navigator.geolocation.getCurrentPosition((position) => {
      var pos = console.log(pos);
      return pos;
    });
  }
}

const center2 = getPosition();
console.log("the returned value is ");
console.log(center2);

var center = {
  lat: 52.4058624,
  lng: -1.507328,
};

function GetBrowserGeoLocation() {
  if (navigator.geolocation) {
    navigator.geolocation.getCurrentPosition(showPosition);
  }
}
function showPosition(position) {
  center.lat = position.coords.latitude;
  center.lng = position.coords.longitude;
}

console.log(center);

const options = {
  mapTypeControl: false,
  fullscreenControl: false,
  streetViewControl: false,
};

let radius = 100;
const url =
  "https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=" +
  center.lat +
  "," +
  center.lng +
  "&radius=" +
  radius +
  "&type=" +
  placeType +
  "&key=" +
  googleAPIKey;

let places = [];
fetch(url)
  .then((res) => {
    return res.json();
  })
  .then((res) => {
    for (let googlePlace of res.results) {
      var place = {};
      var myLat = googlePlace.geometry.location.lat;
      var myLong = googlePlace.geometry.location.lng;
      var coordinate = {
        latitude: myLat,
        longitude: myLong,
      };
      place["placeTypes"] = googlePlace.types;
      place["coordinate"] = coordinate;
      place["placeId"] = googlePlace.place_id;
      place["placeName"] = googlePlace.name;
      places.push(place);
    }
    // Show all the places around 4 km from San Francisco.
    console.log(places);
  })
  .catch((error) => {
    console.log(error);
  });

export default function App() {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries,
  });

  const [markers, setMarkers] = React.useState([]);
  const [selected, setSelected] = React.useState(null);
  const onMapClick = React.useCallback((event) => {
    setMarkers((current) => [
      ...current,
      {
        lat: event.latLng.lat(),
        lng: event.latLng.lng(),
        time: new Date(),
      },
    ]);
  }, []);

  const mapRef = React.useRef();
  const onMapLoad = React.useCallback((map) => {
    mapRef.current = map;
  }, []);

  const panTo = React.useCallback(({ lat, lng }) => {
    mapRef.current.panTo({ lat, lng });
    mapRef.current.setZoom(16);
  }, []);
  if (loadError) return "Error loading maps";
  if (!isLoaded) return "Loading Maps";

  const listItems = places.map((d) => <li key={d.name}>{d.name}</li>);
  return (
    <div className="centered">
      <Search panTo={panTo} />
      <Locate panTo={panTo} />
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        zoom={15}
        center={center}
        options={options}
        onClick={onMapClick}
        onLoad={onMapLoad}
      >
        {markers.map((marker) => (
          <Marker
            key={marker.time.toISOString()}
            position={{ lat: marker.lat, lng: marker.lng }}
            onClick={() => {
              setSelected(marker);
            }}
          />
        ))}

        {selected ? (
          <InfoWindow
            position={{ lat: selected.lat, lng: selected.lng }}
            onCloseClick={() => {
              setSelected(null);
            }}
          >
            <div>
              <h1>Hello</h1>
              <p>Clicked {formatRelative(selected.time, new Date())}</p>
            </div>
          </InfoWindow>
        ) : null}
      </GoogleMap>
      <DisplayPlaces />
    </div>
  );
}
function Locate({ panTo }) {
  return (
    <button
      className="locate"
      onClick={() => {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            panTo({
              lat: position.coords.latitude,
              lng: position.coords.longitude,
            });
          },
          () => null,
          options
        );
      }}
    >
      <img src="compass.svg" alt="compass - locate me" />
    </button>
  );
}

function Search({ panTo }) {
  const {
    ready,
    value,
    suggestions: { status, data },
    setValue,
    clearSuggestions,
  } = usePlacesAutocomplete({
    requestOptions: {
      location: { lat: () => center.lat, lng: () => center.lng },
      radius: 1 * 1000,
    },
  });

  return (
    <div className="search">
      <Combobox
        onSelect={async (address) => {
          setValue(address, false);
          clearSuggestions();
          try {
            const results = await getGeocode({ address });
            const { lat, lng } = await getLatLng(results[0]);
            panTo({ lat, lng });
          } catch (error) {
            console.log("error!");
          }
        }}
      >
        <ComboboxInput
          value={value}
          onChange={(e) => {
            setValue(e.target.value);
          }}
          disable={!ready}
          placeholder="Enter your Point of Interest"
        />
        <ComboboxPopover>
          <ComboboxList>
            {status === "OK" &&
              data.map(({ id, description }) => (
                <ComboboxOption key={id} value={description} />
              ))}
          </ComboboxList>
        </ComboboxPopover>
      </Combobox>
    </div>
  );
}
function DisplayPlaces() {
  return (
    <ul>
      {places.map((nearbyPlaces) => (
        <li key={nearbyPlaces.placeId}>{nearbyPlaces.placeName}</li>
      ))}
    </ul>
  );
}
