import React, { Component } from "react";
import { center, GetBrowserGeoLocation } from "./getBrowserLocation";
import { Card, Accordion } from "react-bootstrap";

const placeType = ["point_of_interest"];

GetBrowserGeoLocation();
console.log(center);

export let radius = 200;

const url =
  "https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=" +
  52.4058624 +
  "," +
  -1.507328 +
  "&radius=" +
  radius +
  "&type=" +
  placeType +
  "&key=" +
  process.env.REACT_APP_GOOGLE_MAPS_API_KEY;
console.log(url);

let places = [];
fetch(url)
  .then((res) => {
    return res.json();
  })
  .then((res) => {
    for (let googlePlace of res.results) {
      var place = {};
      var myLat = googlePlace.geometry.location.lat;
      var myLong = googlePlace.geometry.location.lng;
      var coordinate = {
        latitude: myLat,
        longitude: myLong,
      };
      place["placeTypes"] = googlePlace.types;
      place["coordinate"] = coordinate;
      place["placeId"] = googlePlace.place_id;
      place["placeName"] = googlePlace.name;
      place["formattedAddress"] = googlePlace.photos;
      places.push(place);
    }
    console.log(places);
  })
  .catch((error) => {
    console.log(error);
  });

export class DisplayPlacesList extends Component {
  render() {
    return (
      <div>
        <Accordion defaultActiveKey="0">
          {places.map((nearbyPlaces) => (
            <Card>
              <Accordion.Toggle as={Card.Header} eventKey="0">
                {nearbyPlaces.placeName}
              </Accordion.Toggle>
              <Accordion.Collapse eventKey="0">
                <Card.Body>{nearbyPlaces.address}</Card.Body>
              </Accordion.Collapse>
            </Card>
          ))}
        </Accordion>
      </div>
    );
  }
}
