import React from "react";
import { Map } from "./components/map";
import {
  GoogleMap,
  useLoadScript,
  Marker,
  InfoWindow,
} from "@react-google-maps/api";
import usePlacesAutocomplete, {
  getGeocode,
  getLatLng,
} from "use-places-autocomplete";
import {
  Combobox,
  ComboboxInput,
  ComboboxPopover,
  ComboboxList,
  ComboboxOption,
} from "@reach/combobox";
import { formatRelative } from "date-fns";

import "@reach/combobox/styles.css";
import { googleAPIKey, placeType } from "./GooglePlacesAPI";
const libraries = ["places"];
const mapContainerStyle = {
  width: "100vw",
  height: "50vh",
};

var center = {
  lat: 52.406215,
  lng: -1.529193,
};

console.log(center);

const options = {
  mapTypeControl: false,
  fullscreenControl: false,
  streetViewControl: false,
};

let radius = 100;
const url =
  "https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=" +
  center.lat +
  "," +
  center.lng +
  "&radius=" +
  radius +
  "&type=" +
  placeType +
  "&key=" +
  googleAPIKey;

let places = [];
fetch(url)
  .then((res) => {
    return res.json();
  })
  .then((res) => {
    for (let googlePlace of res.results) {
      var place = {};
      var myLat = googlePlace.geometry.location.lat;
      var myLong = googlePlace.geometry.location.lng;
      var coordinate = {
        latitude: myLat,
        longitude: myLong,
      };
      place["placeTypes"] = googlePlace.types;
      place["coordinate"] = coordinate;
      place["placeId"] = googlePlace.place_id;
      place["placeName"] = googlePlace.name;
      places.push(place);
    }
    // Show all the places around 4 km from San Francisco.
    console.log(places);
  })
  .catch((error) => {
    console.log(error);
  });

function App() {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries,
  });

  const [markers, setMarkers] = React.useState([]);
  const [selected, setSelected] = React.useState(null);
  const onMapClick = React.useCallback((event) => {
    setMarkers((current) => [
      ...current,
      {
        lat: event.latLng.lat(),
        lng: event.latLng.lng(),
        time: new Date(),
      },
    ]);
  }, []);

  const mapRef = React.useRef();
  const onMapLoad = React.useCallback((map) => {
    mapRef.current = map;
  }, []);

  const panTo = React.useCallback(({ lat, lng }) => {
    mapRef.current.panTo({ lat, lng });
    mapRef.current.setZoom(16);
  }, []);
  if (loadError) return "Error loading maps";
  if (!isLoaded) return "Loading Maps";

  const listItems = places.map((d) => <li key={d.name}>{d.name}</li>);
  return <Map></Map>;
}

export default App;

function DisplayPlaces() {
  return (
    <ul>
      {places.map((nearbyPlaces) => (
        <li key={nearbyPlaces.placeId}>{nearbyPlaces.placeName}</li>
      ))}
    </ul>
  );
}
