import React from "react";
export const onMapClick = React.useCallback((event) => {
  setMarkers((current) => [
    ...current,
    {
      lat: event.latLng.lat(),
      lng: event.latLng.lng(),
      time: new Date(),
    },
  ]);
}, []);

export const mapRef = React.useRef();
export const onMapLoad = React.useCallback((map) => {
  mapRef.current = map;
}, []);

export const panTo = React.useCallback(({ lat, lng }) => {
  mapRef.current.panTo({ lat, lng });
  mapRef.current.setZoom(17);
}, []);
