import React from "react";
import {
  GoogleMap,
  useLoadScript,
  Marker,
  InfoWindow,
} from "@react-google-maps/api";
import usePlacesAutocomplete, {
  getGeocode,
  getLatLng,
} from "use-places-autocomplete";
import {
  Combobox,
  ComboboxInput,
  ComboboxPopover,
  ComboboxList,
  ComboboxOption,
} from "@reach/combobox";
import { formatRelative } from "date-fns";

import "@reach/combobox/styles.css";
import { googleAPIKey } from "./GooglePlacesAPI";

const libraries = ["places"];
const mapContainerStyle = {
  width: "100vw",
  height: "50vh",
};
const center = {
  lat: 52.408054,
  lng: -1.510556,
};
const options = {
  mapTypeControl: false,
  fullscreenControl: false,
  streetViewControl: false,
};
const latitude = 37.773972;
const longitude = -122.431297;

let radius = 1 * 1000;
const url =
  "https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=" +
  latitude +
  "," +
  longitude +
  "&radius=" +
  radius +
  //  '&type=' +
  //  placeType +
  "&key=" +
  googleAPIKey;

export default function App() {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries,
  });

  const [markers, setMarkers] = React.useState([]);
  const [selected, setSelected] = React.useState(null);
  const onMapClick = React.useCallback((event) => {
    setMarkers((current) => [
      ...current,
      {
        lat: event.latLng.lat(),
        lng: event.latLng.lng(),
        time: new Date(),
      },
    ]);
  }, []);

  const mapRef = React.useRef();
  const onMapLoad = React.useCallback((map) => {
    mapRef.current = map;
  }, []);

  const panTo = React.useCallback(({ lat, lng }) => {
    mapRef.current.panTo({ lat, lng });
    mapRef.current.setZoom(16);
  }, []);
  if (loadError) return "Error loading maps";
  if (!isLoaded) return "Loading Maps";

  return (
    <div className="centered">
      <Search panTo={panTo} />
      <Locate panTo={panTo} />
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        zoom={15}
        center={center}
        options={options}
        onClick={onMapClick}
        onLoad={onMapLoad}
      >
        {markers.map((marker) => (
          <Marker
            key={marker.time.toISOString()}
            position={{ lat: marker.lat, lng: marker.lng }}
            onClick={() => {
              setSelected(marker);
            }}
          />
        ))}

        {selected ? (
          <InfoWindow
            position={{ lat: selected.lat, lng: selected.lng }}
            onCloseClick={() => {
              setSelected(null);
            }}
          >
            <div>
              <h1>Hello</h1>
              <p>Clicked {formatRelative(selected.time, new Date())}</p>
            </div>
          </InfoWindow>
        ) : null}
      </GoogleMap>
    </div>
  );
}
fetchNearestPlacesFromGoogle = () => {
  const latitude = 25.0756; // you can update it with user's latitude & Longitude
  const longitude = 55.1454;
  let radMetter = 2 * 1000; // Search withing 2 KM radius

  const url =
    "https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=" +
    latitude +
    "," +
    longitude +
    "&radius=" +
    radMetter +
    "&key=" +
    googleAPIKey;

  fetch(url)
    .then((res) => {
      return res.json();
    })
    .then((res) => {
      var places = []; // This Array WIll contain locations received from google
      for (let googlePlace of res.results) {
        var place = {};
        var lat = googlePlace.geometry.location.lat;
        var lng = googlePlace.geometry.location.lng;
        var coordinate = {
          latitude: lat,
          longitude: lng,
        };

        var gallery = [];

        if (googlePlace.photos) {
          for (let photo of googlePlace.photos) {
            var photoUrl = Urls.GooglePicBaseUrl + photo.photo_reference;
            gallery.push(photoUrl);
          }
        }

        place["placeTypes"] = googlePlace.types;
        place["coordinate"] = coordinate;
        place["placeId"] = googlePlace.place_id;
        place["placeName"] = googlePlace.name;
        place["gallery"] = gallery;

        places.push(place);
      }

      // Do your work here with places Array
    })
    .catch((error) => {
      console.log(error);
    });
};
function Locate({ panTo }) {
  return (
    <button
      className="locate"
      onClick={() => {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            panTo({
              lat: position.coords.latitude,
              lng: position.coords.longitude,
            });
          },
          () => null,
          options
        );
      }}
    >
      <img src="compass.svg" alt="compass - locate me" />
    </button>
  );
}

function Search({ panTo }) {
  const {
    ready,
    value,
    suggestions: { status, data },
    setValue,
    clearSuggestions,
  } = usePlacesAutocomplete({
    requestOptions: {
      location: { lat: () => 43.6532, lng: () => -79.3832 },
      radius: 1 * 1000,
    },
  });

  return (
    <div className="search">
      <Combobox
        onSelect={async (address) => {
          setValue(address, false);
          clearSuggestions();
          try {
            const results = await getGeocode({ address });
            const { lat, lng } = await getLatLng(results[0]);
            panTo({ lat, lng });
          } catch (error) {
            console.log("error!");
          }
        }}
      >
        <ComboboxInput
          value={value}
          onChange={(e) => {
            setValue(e.target.value);
          }}
          disable={!ready}
          placeholder="Enter your Point of Interest"
        />
        <ComboboxPopover>
          <ComboboxList>
            {status === "OK" &&
              data.map(({ id, description }) => (
                <ComboboxOption key={id} value={description} />
              ))}
          </ComboboxList>
        </ComboboxPopover>
      </Combobox>
    </div>
  );
}
